import { useNavigate } from "react-router-dom";
function BackdropExample() {
  const navigate = useNavigate();
  const RedirectToHome = () => {
    onClose();
    setTimeout(function () {
      navigate("/");
    }, 2000);
  };
  const OverlayTwo = () => (
    <ModalOverlay
      bg="none"
      backdropFilter="auto"
      backdropInvert="80%"
      backdropBlur="2px"
    />
  );

  const { isOpen, onOpen, onClose } = useDisclosure();
  const [overlay, setOverlay] = React.useState(<OverlayTwo />);

  return (
    <>
      <Button
        h="50px"
        mt="20px"
        maxWidth={"400px"}
        borderRadius="md"
        bg="#e53e3e"
        _hover={{ bg: "#e53e3e" }}
        variant="ghost"
        type="submit"
        color={"white"}
        fontWeight={"bold"}
        onClick={() => {
          setOverlay(<OverlayTwo />);
          onOpen();
        }}
      >
        Place Order Now
      </Button>
      <Modal isCentered isOpen={isOpen} onClose={onClose}>
        {overlay}
        <ModalContent>
          <ModalHeader>Order Success</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <Text>Thank You! Order Details sent on provided email Id</Text>
          </ModalBody>
          <ModalFooter>
            <Button color={"white"} bg="#e53e3e" onClick={RedirectToHome}>
              Ok
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
}
